import React from 'react';
import DropdownButton from 'react-bootstrap/DropdownButton';
import Dropdown from 'react-bootstrap/Dropdown';

class Location extends React.Component {
  constructor(props) {
    super(props);
  }

  render = () => {
    return (
      <div className="locationListItem">
        <h3>{this.props.location.name}</h3>
        <div>{this.props.location.address} {this.props.location.suite}<br/>{this.props.location.city}, {this.props.location.state} {this.props.location.zip}</div>
        <div>
          <DropdownButton className="Button__StyledButton-sc-1uvogdt-0 eDBmXb btn btn-brand" title="Order Here">
            {this.props.location.services.map((entry, i) => {
              return  <Dropdown.Item href="{this.props.location.link}/{entry}" key={"dropdown_"+i}>{entry}</Dropdown.Item>
              })
            }
          </DropdownButton>
        </div>
        <hr className="locationListItem-break"/>
      </div>
    );
  }
}

export default Location;
