import React from 'react';
import {BrowserRouter as Router, NavLink} from 'react-router-dom';
import * as utils from './utils.js';
import Home from './Components/Home.js';
import './App.css';
import ReactGA from 'react-ga';

class App extends React.Component {
  constructor(props) {
    super(props);

    this.homeRef = {};
    const Config  = require('json-loader!./config.json');
    this.state = {
      API: Config['apiAddress'],
      error: {},
      locations: {}
    }
  }

  componentDidMount() {
    utils.ApiRequest(this.state.API)
      .then(data => {
        if (data.locations) {
          this.setState({
            'locations': data.locations
          });
        } else {
          this.setState({
            error: {
              message: `<div className="error">Sorry, an unexpected error occurred</div>`
            }
          });
        }
      })
      /*.catch((e) => {
        console.log(e);
        this.setState({
          error: e
        });
      }*/
  }

  render = () => {
    ReactGA.initialize(Config['ga-tag']);
    ReactGA.pageview(window.location.pathname + window.location.search);
    return (
      <Router>
        <div className="site-content__main">
          <div className="alert alert-js">I'm still being built!</div>
          <Home Config={this.state.Config} locations={this.state.locations} error={this.state.error} ref={(ref) => this.homeRef = ref} API={this.state.API} />
        </div>
      </Router>
    );
  }
}

export default App;
