import React from 'react';
//import DropdownButton from 'react-bootstrap/DropdownButton';
//import Button from 'react-bootstrap/Button'
import Container from 'react-bootstrap/Container'
//import Col from 'react-bootstrap/Col'
//import Row from 'react-bootstrap/Row'
import Alert from 'react-bootstrap/Alert'
import MenuGroup from './MenuGroup';
import Card from 'react-bootstrap/Card'
import Nav from 'react-bootstrap/Nav'

class Menu extends React.Component {
    constructor(props) {
      super(props);
      this.state = {
        error: false,
        location: {services:[]},
        menus:{}
      };
      this.componentDidUpdate({locations: {}, location: {services:[]}});
    }
    componentDidUpdate(prevProps) {
      if (prevProps.locations.length !== this.props.locations.length) {
        this.props.locations.map((entry, i) => {
            if (entry.link === this.props.match.params.miniBar) {
              this.setState({
                location: entry
              });
              entry.services.map((service,i) => {
                if(service.name === this.props.match.params.service){
                  this.setState({
                    menus: service.menus
                  });
                }
              })
            }
          });

          if (!this.state.location){
            this.setState({
              error: "Location Not Found"
            });
          }
      }
    }
  render() {
      return (
      <Container>
      <Card>
        <Card.Header>
        <Nav variant="pills" defaultActiveKey="#group0">
        {this.state.menus.length && this.state.menus.map((entry, i) => {
          return entry.map((menu, ia) => {
            return (
            <Nav.Item key={"group"+ia}>
              <Nav.Link href={"#group"+ia}>{menu.name}</Nav.Link>
            </Nav.Item>)
          })
        })
      }
          </Nav>
        </Card.Header>
        <Card.Body>
          <Card.Title>Special title treatment</Card.Title>
          <Card.Text>
            With supporting text below as a natural lead-in to additional content.
          </Card.Text>
        </Card.Body>
        </Card>
      </Container>
    );
  }

}
export default Menu;
