import React from 'react';
import {BrowserRouter as Router, NavLink} from 'react-router-dom';
import * as utils from './utils.js';
import Home from './Components/Home.js';
import './App.css';
import ReactGA from 'react-ga';
import Config from './config.json';
import 'bootstrap/dist/css/bootstrap.min.css';
import Jumbotron from 'react-bootstrap/Jumbotron';
import Toast from 'react-bootstrap/Toast';
import Container from 'react-bootstrap/Container';
import Button from 'react-bootstrap/Button';

class App extends React.Component {
  constructor(props) {
    super(props);

    this.homeRef = {};
    this.state = {
      API: "https://localhost/api/",
      error: {},
      resume: {}
    }
  }

  componentDidMount() {
    utils.ApiRequest(this.state.API + "index.php")
      .then(data => {
        if (data.success) {
          this.setState({
            'resume': data.resume
          });
        } else {
          this.setState({
            error: {
              message: `<div className="error">Sorry, an unexpected error occurred</div>`
            }
          });
        }
      })
      /*.catch((e) => {
        console.log(e);
        this.setState({
          error: e
        });
      }*/
  }

  render = () => {
    ReactGA.initialize(Config['ga-tag']);
    ReactGA.pageview(window.location.pathname + window.location.search);
    return (
      <Router>
        <div className="App">
          <div className="contact-card">
            <ul className="nav-container">
              <nav className="nav">
                <NavLink to={'/'} className="nav-button">Home</NavLink>
                <NavLink to={'/work-history'} className="nav-button">Employment</NavLink>
                <NavLink to={'/education'} className="nav-button">Education</NavLink>
                <NavLink to={'/contact'} className="nav-button">Contact</NavLink>
              </nav>
            </ul>
          </div>
          <Home resume={this.state.resume} error={this.state.error} ref={(ref) => this.homeRef = ref} API={this.state.API} />
        </div>
      </Router>
    );
  }
}

export default App;
