import React from 'react';
import DropdownButton from 'react-bootstrap/DropdownButton';
import Dropdown from 'react-bootstrap/Dropdown';
import Container from 'react-bootstrap/Container'
import Col from 'react-bootstrap/Col'
import Row from 'react-bootstrap/Row'

class Order extends React.Component {
    constructor(props) {
      super(props);
      this.state = {
        error: false,
        location: {}
      };
    }

      componentDidUpdate(prevProps) {
        if (prevProps.locations.length !== this.props.locations.length) {
          this.props.locations.map((entry, i) => {
              if (entry.name === this.props.match.params.miniBar) {
                this.setState({
                  location: entry
                });
                this.state.location = this.props.match.params.miniBar;
              }
            });

            if(!this.state.location){
              this.state.error="Location Not Found";
            }
        }
      }

      render() {
        if (this.state.error) {
          return <div>{this.state.error}</div>
        }

        return ( <Container fluid >
          <Row >
          <Col >
          <h1> Order for {this.state.location} </h1>

          </Col>
          </Row>
          </Container>
        );
      }
    }
    export default Order;
