import React from 'react';
// eslint-disable-next-line
import {BrowserRouter as Router, Route, Switch} from 'react-router-dom';
import BeatLoader from 'react-spinners/ClipLoader';
import GoogleMap from 'google-map-react';

class Home extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      API: props.API,
      error: "",
      locations_loaded: false,
      locations: {}
    }
  }

  componentDidUpdate(prevProps) {
    if (prevProps.locations.length !== this.props.locations.length) {
      this.setState({
        locations: this.props.locations
      });
    }
  }

  setError = (e) => {
    this.setState({
      error: e
    });
  }

  NoMatch = ({ location }) => (
    <div className="error">Could not find <code>{location.pathname.substring(1)}</code></div>
  )
  ShowLocations = () => {
    return this.state.locations.map((entry, i) => {
      return (
        <div>
          <h3>{entry.name}</h3>
          <div>{entry.address} {entry.suite}{"\n"}{entry.city}, {entry.state} {entry.zip}</div>
          <div><button className="Button__StyledButton-sc-1uvogdt-0 eDBmXb btn btn-brand">Order </button></div>
        </div>
      );
    });
  }

  render = () => {
    if (this.state.error) {
      return <div className="error">{this.state.error}</div>;
    }

    if (this.state.locations.length && this.props.Config) {
      return (
        <div className="main-content">
          <div className="row" className="mapContainer">
            <div className="col-sm-4" style={{ height: '600px'}}>
              <h2>Locations</h2>
              <div style={{ height: '100vh',overflowY : 'auto'}}>
              <this.ShowLocations />
              </div>
            </div>
            <div className="col-sm-8" style={{ height: '600px'}}>
              <GoogleMap
               bootstrapURLKeys={{key:this.props.Config['mapAPI']}} // set if you need stats etc ...
               defaultCenter={[41.881832, -87.623177]}
               defaultZoom={9}>
             </GoogleMap>
            </div>
          </div>
        </div>
      );
    } else {
      return (
        <div className='sweet-loading'>
          <BeatLoader sizeUnit={"px"} size={150} color={'#123abc'} loading={!this.state.locations.length} />
        </div>
      );
    }
  }
}

export default Home;
