import React from 'react';
//import DropdownButton from 'react-bootstrap/DropdownButton';
//import Button from 'react-bootstrap/Button'
import Container from 'react-bootstrap/Container'
//import Col from 'react-bootstrap/Col'
//import Row from 'react-bootstrap/Row'
import Alert from 'react-bootstrap/Alert'
import MenuGroup from './MenuGroup';
import Accordion from 'react-bootstrap/Accordion'


class Menu extends React.Component {
    constructor(props) {
      super(props);
      this.state = {
        error: false,
        location: {services:[]},
        menus:{}
      };
      this.componentDidUpdate({locations: {}, location: {services:[]}});
    }
    componentDidUpdate(prevProps) {
      if (prevProps.locations.length !== this.props.locations.length) {
        this.props.locations.map((entry, i) => {
            if (entry.link === this.props.match.params.miniBar) {
              this.setState({
                location: entry
              });
              entry.services.map((service,i) => {
                if(service.name === this.props.match.params.service){
                  this.setState({
                    menus: service.menus
                  });
                }
              })
            }
          });

          if (!this.state.location){
            this.setState({
              error: "Location Not Found"
            });
          }
      }
    }
  render() {
      return (
      <Container fluid >
      <Accordion>
        <Card>
          <Card.Header>
            <Accordion.Toggle as={Button} variant="link" eventKey="0">
              Click me!
            </Accordion.Toggle>
          </Card.Header>
          <Accordion.Collapse eventKey="0">
            <Card.Body>Hello! I'm the body</Card.Body>
          </Accordion.Collapse>
        </Card>
        <Card>
          <Card.Header>
            <Accordion.Toggle as={Button} variant="link" eventKey="1">
              Click me!
            </Accordion.Toggle>
          </Card.Header>
          <Accordion.Collapse eventKey="1">
            <Card.Body>Hello! I'm another body</Card.Body>
          </Accordion.Collapse>
        </Card>
      </Accordion>
              {this.state.menus.length && this.state.menus.map((entry, i) => {
          return entry.map((menu, ia) => <MenuGroup key={"menugroup"+ia} menugroup={menu}/>)
        })
      }
      </Container>
    );
  }

}
export default Menu;
