import React from 'react';
import {BrowserRouter as Router, Route, Switch} from 'react-router-dom';
import * as utils from './utils.js';
import Home from './Components/Home.js';
import './App.css';
import ReactGA from 'react-ga';
import Order from './Components/Order.js';

class App extends React.Component {
  constructor(props) {
    super(props);
    const Config = require('./config.json');

    this.homeRef = {};
    this.state = {
      Config: Config,
      API: Config['apiAddress'],
      error: {},
      locations: {}
    }
  }

  componentDidMount() {
    utils.ApiRequest(this.state.API)
      .then(data => {
        if (data.locations) {
          this.setState({
            'locations': data.locations
            });
        }   else {
          this.setState({
              message: `<div className="error">Sorry, an unexpected error occurred</div>`
            });
        }
      })
      /*.catch((e) => {
        console.log(e);
        this.setState({
          error: e
        });
      }*/
  }

  render = () => {
    ReactGA.initialize(this.state.Config['ga-tag']);
    ReactGA.pageview(window.location.pathname + window.location.search);
    return (
        <Switch>
          <Route exact strict path={`/`} render={({ match }) => <Home Config={this.state.Config} locations={this.state.locations} error={this.state.error} ref={(ref) => this.homeRef = ref} API={this.state.API} />} />
          <Route path={`/order/:miniBar`} component={Order} />
        </Switch>
    );
  }
}

export default App;
