import React from 'react';
//import DropdownButton from 'react-bootstrap/DropdownButton';
//import Dropdown from 'react-bootstrap/Dropdown';
import Container from 'react-bootstrap/Container'
import Col from 'react-bootstrap/Col'
import Row from 'react-bootstrap/Row'

class Order extends React.Component {
    constructor(props) {
      super(props);
      this.state = {
        error: false,
        location: {}
      };
      this.componentDidUpdate({locations: {}});
    }

      componentDidUpdate(prevProps) {
        if (prevProps.locations.length !== this.props.locations.length) {
          this.props.locations.map((entry, i) => {
              if (entry.name === this.props.match.params.miniBar) {
                this.setState({
                  location: entry
                });
              }
            });

            if (!this.state.location.link){
              this.setState({
                error: "Location Not Found"
              });
            }
        }
      }

      render() {
        if (this.state.error !== false) {
          return <div>{this.state.error}</div>
        }

        if (this.props.locations.length) {
        return ( <Container fluid >
          <Row >
          <Col >
          <h2>Order for {this.state.location.name}</h2>

          </Col>
          </Row>
          </Container>
        );
      } else {
        return <div>No locations</div>
      }
      }
    }
    export default Order;
