import React from 'react';
//import DropdownButton from 'react-bootstrap/DropdownButton';
//import Button from 'react-bootstrap/Button'
import Container from 'react-bootstrap/Container'
import Col from 'react-bootstrap/Col'
import Row from 'react-bootstrap/Row'
import Alert from 'react-bootstrap/Alert'

class Menu extends React.Component {
    constructor(props) {
      super(props);
      this.state = {
        error: false,
        location: {services:[]},
        menus:{}
      };
      this.componentDidUpdate({locations: {}, location: {services:[]}});
    }
    componentDidUpdate(prevProps) {
      if (prevProps.locations.length !== this.props.locations.length) {
        this.props.locations.map((entry, i) => {
            if (entry.link === this.props.match.params.miniBar) {
              this.setState({
                location: entry
              });
              entry.services.map((service,i) => {
                if(menu.service === this.props.match.params.service){
                  this.setState({
                    menus: service
                  });
                }
              })
            }
          });

          if (!this.state.location){
            this.setState({
              error: "Location Not Found"
            });
          }
      }
    }
  render() {
      return (
      <Container fluid >
        <Alert variant="warning">Menu Goes Here!</Alert>
        {this.state.menus.menu.map((entry, i) => {

        })
      }
      </Container>
    );
  }

}
export default Menu;
