import React from 'react';
// eslint-disable-next-line
import {BrowserRouter as Router, Route, Switch} from 'react-router-dom';
import BeatLoader from 'react-spinners/ClipLoader';
import GoogleMap from 'google-map-react';

class Home extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      API: props.API,
      error: "",
      locations_loaded: false,
      locations: {}
    }
  }

  componentDidUpdate(prevProps) {
    if (prevProps.locations.length !== this.props.locations.length) {
console.log(this.props.locations);
      this.setState({
        locations: this.props.locations
      });
    }
  }

  setError = (e) => {
    this.setState({
      error: e
    });
  }

  NoMatch = ({ location }) => (
    <div className="error">Could not find <code>{location.pathname.substring(1)}</code></div>
  )

  render = () => {
    if (this.state.error) {
      return <div className="error">{this.state.error}</div>;
    }

console.log((this.state.locations.length) ? "done" : "not done");
    if (this.state.locations.length) {
      const mapCenter = [41.881832, -87.623177];
      return (
        <div className="main-content">
          <div className="row">
            <div className="col-sm-4"></div>
            <div className="col-sm-8">
              <GoogleMap
               apiKey={this.props.Config['mapAPI']} // set if you need stats etc ...
               center={mapCenter}
               zoom={9}>
             </GoogleMap>
            </div>
          </div>
        </div>
      );
    } else {
      return (
        <div className='sweet-loading'>
          <BeatLoader sizeUnit={"px"} size={150} color={'#123abc'} loading={!this.state.locations.length} />
        </div>
      );
    }
  }
}

export default Home;
