import React from 'react';
//import DropdownButton from 'react-bootstrap/DropdownButton';
//import Button from 'react-bootstrap/Button'
//import Container from 'react-bootstrap/Container'
import Col from 'react-bootstrap/Col'
//import Image from 'react-bootstrap/Image'
//import Row from 'react-bootstrap/Row'
//import Alert from 'react-bootstrap/Alert'
import Card from 'react-bootstrap/Card'


class MenuItem extends React.Component {
  constructor(props) {
    super(props);
  }

  render = () => {
    if(this.props.items.image===""){
      return <></>
    }else{
    return (
          <Col lg={4}>
            <Card style={{ width: '18rem' }}>
              <Card.Img variant="top" src={this.props.items.image} style={{width:"286px",height:"180px"}} />
              <Card.Body>
                <Card.Title><h3>{this.props.items.name}</h3></Card.Title>
                <Card.Subtitle>{this.props.items.price}</Card.Subtitle>
                <Card.Text>
                  {this.props.items.description}
                </Card.Text>
              </Card.Body>
            </Card>
          </Col>
    );}
  }
}

export default MenuItem;
