import React from 'react';
// eslint-disable-next-line
import {BrowserRouter as Router, Route, Switch} from 'react-router-dom';
import BeatLoader from 'react-spinners/ClipLoader';

class Home extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      API: props.API,
      error: "",
      resume_loaded: false,
      resume: {}
    }
  }

  setError = (e) => {
    this.setState({
      error: e
    });
  }

  NoMatch = ({ location }) => (
    <div className="error">Could not find <code>{location.pathname.substring(1)}</code></div>
  )

  render = () => {
    if (Object.keys(this.props.error).length) {
      return <div className="error">{this.state.error}</div>;
    }

    if (this.props.hasOwnProperty('resume') && Object.keys(this.props.resume).length) {
      const resume = this.props.resume;
      return (
        <div className="main-content">
          <Switch>
            <Route exact strict path={'/'} render={({ match }) => <div className="home-background">Foo foo foo foo</div>} />
            <Route exact path={'/work-history'} key={'workhistory'} render={({ match }) => <div><h2>Employment</h2><WorkHistory work_history={resume.work_history} /></div>} />
            <Route exact path={'/education'} key={'education'} render={({ match }) => <div><h2>Education</h2><Education education={resume.education} /><h2>Certifications</h2><Certifications certifications={resume.certifications} /></div>} />
            <Route exact path={'/contact'} key={'contact'} render={({ match }) => <div className="contact-div"><Contact API={this.state.API} /></div>} />
            <Route component={this.NoMatch} />
          </Switch>
        </div>
      );
    } else {
      return (
        <div className='sweet-loading'>
          <BeatLoader sizeUnit={"px"} size={150} color={'#123abc'} loading={!this.state.resume_loaded} />
        </div>
      );
    }
  }
}

export default Home;
